  let(:valid_session) { {} }

  describe 'GET #index' do
    it 'returns a success response' do
      Retrospective.create! valid_attributes
      get :index, params: {}, session: valid_session
      expect(response).to be_successful
    end
  end

  describe 'GET #show' do
    it 'returns a success response' do
      retrospective = Retrospective.create! valid_attributes
      get :show, params: { id: retrospective.to_param }, session: valid_session
      expect(response).to be_successful
    end
  end

  describe 'GET #new' do
    it 'returns a success response' do
      get :new, params: {}, session: valid_session
      expect(response).to be_successful
    end
  end

  describe 'GET #edit' do
    it 'returns a success response' do
      retrospective = Retrospective.create! valid_attributes
      get :edit, params: { id: retrospective.to_param }, session: valid_session
      expect(response).to be_successful
    end
  end

  describe 'POST #create' do
    context 'with valid params' do
      it 'creates a new Retrospective' do
        expect do
          post :create, params: { retrospective: valid_attributes },
                        session: valid_session
        end.to change(Retrospective, :count).by(1)
      end

      it 'redirects to the created retrospective' do
        post :create, params: { retrospective: valid_attributes },
