        expect(response).to be_successful
      end
    end
  end

  describe 'PUT #update' do # rubocop:disable Metrics/BlockLength
    context 'with valid params' do
      let(:new_attributes) do
        { 'title' => 'test', 'description' => 'test',
          'date' => DateTime.now.to_date }
